	    // Picker Handling
	    {
	        var getEventPos = function(element, evt) {
	            var rect = element.getBoundingClientRect();
	            return {
	              x: (evt.touches ? evt.touches[0].clientX : evt.clientX) - rect.left,
	              y: (evt.touches ? evt.touches[0].clientY : evt.clientY) - rect.top
	            };
	        };

	        var pickerStartPos = {};
	        
	        var startPickerDragging =  function(evt) {
	            var newMousePos = getEventPos(meConfig.div, evt);
	            pickerStartPos = newMousePos;
	            isPickerDragged = true;
	            evt.preventDefault();
	        };
	        
	        var performPickerDragging = function(evt) {
	            if(!isPickerDragged) return;
	            evt.preventDefault();
	            var newMousePos = getEventPos(meConfig.div, evt);
	            var deltaX = newMousePos.x - pickerStartPos.x;
	            var deltaY = newMousePos.y - pickerStartPos.y;

	            var divRect = meConfig.div.getBoundingClientRect();
	            var newPickerX = pickerPosition.left + deltaX;
	            var newPickerY = divRect.height - pickerPosition.bottom + deltaY;
	            var geoCoords = meteoEarth.screenToGeoCoords(newPickerX, newPickerY);

	            if(geoCoords[2] < 0.0 ) {
	                isPickerDragged = false;
	                $timeout(function () {
	                    $scope.showPicker = false;
	                });
	                return;
	            }
	            meteoEarth.setPickerPosition(geoCoords[0], geoCoords[1]);

	            $timeout(function () {
	                $scope.pickerStyle.left = newPickerX;
	                $scope.pickerStyle.bottom = divRect.height - newPickerY + pinHeight;
	            });
	        };
	        
	        var stopPickerDragging = function(evt) {
	            isPickerDragged = false;
	        };

	        document.getElementById('picker').addEventListener( "mousedown", startPickerDragging);
	        document.getElementById('picker').addEventListener( "touchstart", startPickerDragging);
	        document.addEventListener( "mousemove", performPickerDragging);
	        document.addEventListener( "touchmove", performPickerDragging);
	        document.addEventListener( "mouseup", stopPickerDragging);
	        document.addEventListener( "touchend", stopPickerDragging);
	    }

