	        if( meConfig.viewSettings.isWindEnabled ) { htmlCode += ", isWindEnabled:true" };
	        if( meConfig.viewSettings.isCloudEnabled ) { htmlCode += ", isCloudEnabled:true" };
	        if( meConfig.viewSettings.isPressureEnabled ) { htmlCode += ", isPressureEnabled:true" };
	        if( meConfig.viewSettings.isTropicalStormEnabled ) { htmlCode += ", isTropicalStormEnabled:true" };
